mapper.select:
SELECT info_id, title, content, writer, view_cnt, comment_cnt, reg_date
FROM information
WHERE info_id = #{info_id}

mapper.update:
UPDATE information
SET title = #{title}
    , content = #{content}
    , up_date = now()
WHERE info_id = #{info_id}

UPDATE information
SET view_cnt = view_cnt + 1
WHERE info_id = #{info_id}
